#!/bin/bash

echo 1. stdout > /proc/self/fd/1
sleep 1
echo 2. stderr > /proc/self/fd/2
sleep 1
echo 3. stdout > /proc/self/fd/1
sleep 1
echo 4. stderr > /proc/self/fd/2
sleep 1
read -r fs
echo "5. First line of stdin: $fs"
sleep 1
echo "6. sending metrics right now"
echo -ne '{"foo.bar":""}\n{"foo.bar":""}' > /proc/self/fd/3
sleep 1
echo "closing stderr"
exec 2>&-
echo "7. wc rest of stdin"
wc
echo "8. wc rest of stdin"
wc
echo "closing stdout, then sending some more metrics, then exiting with 75"
exec 1>&-
echo -ne "metrics without newline. here is a tab instead: \x09" > /proc/self/fd/3
exec 3>&-
sleep 0.5
exit 75
